mapper.select:
select user_id userId, qq_uid qqUid, wb_uid wbUid, wx_uid wxUid
from kjuns_user_account where 1=1
 and mobile_phone = #{mobilePhone} 
 and qq_uid = #{qqUid} 
 and wb_uid = #{wbUid} 
 and wx_uid = #{wxUid} 
 and token = #{token} 
and data_flag = 1

mapper.update:
update kjuns_user_account

	 user_id = #{userId}
	 mobile_phone = #{mobilePhone} 
	 qq_uid = #{qqUid} 
	 wb_uid = #{wbUid} 
	 wx_uid = #{wxUid} 

where token = #{token}

update kjuns_user_account 

	 qq_uid = #{qqUid} 
		wb_uid = #{wbUid} 
	 wx_uid = #{wxUid} 
	 wx_unionid = #{wxUnionid}
	 rr_uid = #{rrUid} 
	 fb_uid = #{fbUid} 
	 twi_uid = #{twiUid} 

where mobile_phone = #{mobilePhone}
